        "import streamlit as st\n",
        "import joblib\n",
        "import nltk\n",
        "import re\n",
        "from nltk.tokenize import word_tokenize\n",
        "from nltk.corpus import stopwords\n",
        "from nltk.stem import WordNetLemmatizer\n",
        "from sklearn.feature_extraction.text import TfidfVectorizer\n",
        "from sklearn.linear_model import LogisticRegression\n",
        "\n",
        "# Download NLTK data\n",
        "\n",
        "def download_nltk_data():\n",
        "    nltk.download('punkt')\n",
        "    nltk.download('stopwords')\n",
        "    nltk.download('wordnet')\n",
        "\n",
        "download_nltk_data()\n",
        "\n",
        "# Load the model and vectorizer\n",
        "\n",
        "def load_model_and_vectorizer():\n",
        "    #Check if files exists otherwise initialize and save them\n",
        "    try:\n",
        "        model = joblib.load('model.joblib')\n",
        "        vectorizer = joblib.load('vectorizer.joblib')\n",
        "    except FileNotFoundError:\n",
        "        # Code to train and save your model and vectorizer if they don't exist\n",
        "        # Assuming you have X_train and y_train from your previous code\n",
        "\n",
        "        # Initialize and train the model\n",
        "        model = LogisticRegression(max_iter=1000)\n",
        "        model.fit(X_train_tfidf, y_train)\n",
        "\n",
        "        # Initialize and fit the vectorizer\n",
        "        vectorizer = TfidfVectorizer()\n",
        "        vectorizer.fit(X_train)\n",
        "\n",
        "        # Save the model and vectorizer\n",
        "        joblib.dump(model, 'model.joblib')\n",
        "        joblib.dump(vectorizer, 'vectorizer.joblib')\n",
        "    return model, vectorizer\n",
        "\n",
        "model, vectorizer = load_model_and_vectorizer()\n",
        "\n",
        "# Text preprocessing function (from your notebook)\n",
        "def text_preprocess(text):\n",
        "    text = text.lower()\n",
        "    text = re.sub(r'[^a-zA-Z\\s]', '', text)\n",
        "    tokens = word_tokenize(text)\n",
        "    stop_words = set(stopwords.words('english'))\n",
        "    tokens = [word for word in tokens if word not in stop_words]\n",
        "    lemmatizer = WordNetLemmatizer()\n",
        "    tokens = [lemmatizer.lemmatize(word) for word in tokens]\n",
        "    return ' '.join(tokens)\n",
        "\n",
        "# Sentiment prediction function (from your notebook)\n",
        "def predict_sentiment(review, vectorizer, model):\n",
        "    clean_review = text_preprocess(review)\n",
        "    review_tfidf = vectorizer.transform([clean_review])\n",
        "    prediction = model.predict(review_tfidf)[0]\n",
        "    sentiment = \"Positive\" if prediction == 1 else \"Negative\"\n",
        "    return sentiment\n",
        "\n",
        "# Streamlit app\n",
        "st.set_page_config(\n",
        "    page_title=\"Movie Review Sentiment Analysis\",\n",
        "    page_icon=\"🎬\",\n",
        "    layout=\"centered\"\n",
        ")\n",
        "\n",
        "st.title(\"🎬 Movie Review Sentiment Analysis\")\n",
        "st.markdown(\n",
        "    \"\"\"\n",
        "    Enter a movie review below to predict whether the sentiment is **Positive** or **Negative**.\n",
        "    Built using a Logistic Regression model trained on the IMDB dataset.\n",
        "    \"\"\"\n",
        ")\n",
        "\n",
        "# Input text area\n",
        "review = st.text_area(\n",
        "    \"Enter your movie review:\",\n",
        "    placeholder=\"e.g., This movie was absolutely fantastic and thrilling!\",\n",
        "    height=150\n",
        ")\n",
        "\n",
        "# Predict button\n",
        "if st.button(\"Predict Sentiment\"):\n",
        "    if not review or review.strip() == \"\":\n",
        "        st.error(\"Please enter a valid review.\")\n",
        "    else:\n",
        "        with st.spinner(\"Analyzing sentiment...\"):\n",
        "            sentiment = predict_sentiment(review, vectorizer, model)\n",
        "        st.success(f\"**Sentiment**: {sentiment}\")\n",
        "\n",
        "# Example reviews"
